<?php	ob_start();	$current_file= $_SERVER['SCRIPT_NAME'];	//Starting Session	session_start();		//connecting to database	include 'connection.php';		//Check if any error or success message is there	$error = "";	if(isset($_GET['error']))	{		$error = $_GET['error'];	}		$sucess = "";	if(isset($_GET['sucess']))	{		$sucess = $_GET['sucess'];	}		$userId ='';	$type = "" ;	$emailId = '';	$username= '';	$password= '';	$confirmPassword= '';	$salt= '';	$verifyId= '';	$status =1;		//Validating Existing Session	if(isset($_POST['signUp']))	{			if(isset($_POST['emailId']) && isset($_POST['username']) && isset($_POST['password']) && isset($_POST['confirmPassword']) && isset($_POST['type']) )		{			$emailId= $_POST['emailId'];			$username= $_POST['username'];			$password= $_POST['password'];			$confirmPassword= $_POST['confirmPassword'];			$type = $_POST['type'] ;						if($password == $confirmPassword)			{				$salt=md5($emailId.$password);								$emailQuery = "SELECT Count(emailId) as email FROM `user` WHERE `emailId` = '$emailId' ";				$emailResult = $conn -> query($emailQuery);				$emailData = mysqli_fetch_assoc($emailResult);				$emailCount = $emailData['email'];								if($emailCount == 0)				{					$insertQuery = "INSERT INTO `user`(`type`,`username`, `emailId`, `password`, `salt`,`status`) VALUES ('$type','$username', '$emailId', '$password', '$salt','2')";					$insertResult = $conn -> query($insertQuery);										if($insertResult)					{						$userIdQuery = "SELECT `userId` FROM `user` WHERE `emailId` = '$emailId' ";						$userIdResult = $conn -> query($userIdQuery);						$userIdData = mysqli_fetch_assoc($userIdResult);						$userId = $userIdData['userId'];												$verifyId=md5($emailId.$username.$userId);												$updateQuery = "UPDATE `user` SET `verifyId`='$verifyId' WHERE `userId`='$userId'";						$updateResult = $conn -> query($updateQuery);												if($updateResult)						{							//include 'mailSend.php';						}						else						{							$error = "Insert Query of verifyId is not Working.";							header('Location:register.php?error='.$error);						}					}					else					{							$error = "Insert Query of emailId is not Working.";							header('Location:register.php?error='.$error);					}				}				else				{					$statusQuery = "SELECT `status` FROM `user` WHERE `emailId` = '$emailId' ";					$statusResult = $conn -> query($statusQuery);					$statusData = mysqli_fetch_assoc($statusResult);					$status = $statusData['status'];										if($status == 2)					{						$sucess = "You are already register.Please verify your email.";						header('Location:register.php?sucess='.$sucess);					}					else if($status == 3)					{						$sucess = "You are already register.Please LogIn.";						header('Location:login.php?sucess='.$sucess);					}				}			}			else			{				$error = "Please Enter Same Password For Confirmation.";				header('Location:register.php?error='.$error);			}		}	}	if($type == "company")		header('Location:company-details.php');	if($type == "jobSeeker")		header('Location:resume.php');			//header file				require "common/header.php";?>    <title>Qwiklync Register</title>    </head>    <!-- FOR NAVBAR & BLACK STRIP ON TOP  -->    <?php require "common/navbar.php" ;  ?>        <section class="job-breadcrumb">            <div class="container">                <div class="row">                    <div class="col-md-6 col-sm-7 co-xs-12 text-left">                        <h3>Register</h3>                    </div>                    <div class="col-md-6 col-sm-5 co-xs-12 text-right">                        <div class="bread">                            <ol class="breadcrumb">                                <li><a href="index.php">Home</a> </li>                                <li class="active">Register</li>                            </ol>                        </div>                    </div>            