<?php	ob_start();	$current_file= $_SERVER['SCRIPT_NAME'];	//Starting Session	session_start();		//connecting to database	include 'connection.php';		//Check if any error or success message is there	$error = "";	if(isset($_GET['error']))	{		$error = $_GET['error'];	}		$sucess = "";	if(isset($_GET['sucess']))	{		$sucess = $_GET['sucess'];	}		$emailId = '';	$password= '';	$salt= '';		if(isset($_POST['logIn']))	{			if(isset($_POST['emailId']) && isset($_POST['password']))		{			$emailId= $_POST['emailId'];			$password= $_POST['password'];			$salt=md5($emailId.$password);						$checkQuery = "SELECT count(userId) as Total,`status` FROM `user` WHERE `emailId`='$emailId' AND `salt`='$salt'";			$checkResult = $conn -> query($checkQuery);			$checkData = mysqli_fetch_assoc($checkResult);			$count = $checkData['Total'];						if($count==1)			{					$status = $checkData['status'];								if($status== 2)				{						$error = "Please verify your account.";					header('Location:login.php?error='.$error);				}				else				{						$sucess = "Welcome! You are successfully LogIn.";					header('Location:login.php?sucess='.$sucess);				}			}			else			{						$error = "Invalid LogIn Email Id or Password.";				header('Location:login.php?error='.$error);			}		}	}				    require "common/header.php";?>    <title>Qwiklync Login</title></head>                <!-- FOR NAVBAR & BLACK STRIP ON TOP  -->            <?php require "common/navbar.php" ;  ?>            <section class="job-breadcrumb">            <div class="container">                <div class="row">                    <div class="col-md-6 col-sm-7 co-xs-12 text-left">                        <h3>Login </h3>                    </div>                    <div class="col-md-6 col-sm-5 co-xs-12 text-right">                        <div class="bread">                            <ol class="breadcrumb">                                <li><a href="index.php">Home</a> </li